    public static Color[][][] getColor(int i){
        GreenfootImage map = new GreenfootImage("Map1.bmp");
        int width = map.getWidth()/5-1;
        int height = map.getHeight();
        Color[][][] ret = new Color[width][height][3];
        for(int x=0;x<width;x++){
            for(int y=0;y<height;y++){
                ret[width-x-1][y][0] = map.getColorAt(x+width+1+width+1,y);
                ret[width-x-1][y][1] = map.getColorAt(x+width+1+width+1+width+1,y);
                ret[width-x-1][y][2] = map.getColorAt(x+width+1+width+1+width+1+width+1,y);
            }
        }
        return ret;
    }
   
   
   
   
   
    
    
